// ProfileBanner.js
import React from 'react';
import { ImageBackground, StyleSheet, Image, View, Text, TouchableOpacity } from 'react-native';
import { heightPercentageToDP as hp, widthPercentageToDP as wp } from 'react-native-responsive-screen';
import profilePlacholder from '../assets/profile-placeholder.jpg';

const ProfileBanner = ({ user, onSignOut }) => {
  return (
    <ImageBackground source={require('../assets/profile-background.webp')} style={styles.banner}>
      <TouchableOpacity style={styles.signOutButton} onPress={onSignOut}>
        <Text style={styles.signOutText}>Sign Out</Text>
      </TouchableOpacity>

      <View style={styles.profileContainer}>
        <Image style={styles.profilePlacholder} source={profilePlacholder} />
      </View>
      <Text style={styles.fullName}>{user.fullName}</Text>
    </ImageBackground>
  );
};

const styles = StyleSheet.create({
  banner: {
    width: wp('100%'),
    minHeight: hp('33%'),
    alignItems: 'center',
    justifyContent: 'flex-end',
    paddingBottom: hp('4%'),
    marginBottom: hp('1%'),
    position: 'relative', // To position the button relative to the banner
  },
  profileContainer: {
    width: 150,
    height: 150,
    borderRadius: 75,
    overflow: 'hidden',
  },
  profilePlacholder: {
    width: '100%',
    height: '100%',
  },
  fullName: {
    marginTop: 25,
    fontSize: 18,
    fontWeight: 'bold',
    color: 'white',
  },
  signOutButton: {
    position: 'absolute',
    top: hp('2%'),
    right: wp('2%'),
    padding: 10,
    backgroundColor: 'rgba(255, 255, 255, 0.3)',
    borderRadius: 8,
  },
  signOutText: {
    color: 'white',
    fontWeight: 'bold',
  },
});

export default ProfileBanner;
