// EventFormScreen.js

import React, { useState } from 'react';
import { View, Text, StyleSheet } from 'react-native';
import FlexInput from './FlexInput';
import SubmitButton from './SubmitButton';
import DatePicker from './DatePicker';
import TimePicker from './TimePicker'; // Make sure to import your TimePicker component
import { localTime } from '../utils/formatFunctions';
import eventApi from '../api/eventApi';
import { format } from 'date-fns'; // Import the format function from date-fns

const EventFormScreen = ({ hostId }) => {
  const [eventName, setEventName] = useState('');
  const [description, setDescription] = useState('');
  const [date, setDate] = useState(new Date());
  const [startTime, setStartTime] = useState(new Date());
  const [endTime, setEndTime] = useState(new Date());
  const [capacity, setCapacity] = useState('');
  const [address, setAddress] = useState('');
  console.log(new Date().toLocaleDateString())

  console.log('Host Id: ' + hostId)

  const handleDateChange = (selectedDate) => {
    setDate(selectedDate);
  };

  const handleStartTimeChange = (selectedTime) => {
    setStartTime(selectedTime);
  };

  const handleEndTimeChange = (selectedTime) => {
    setEndTime(selectedTime);
  };

  const handleSubmit = async () => {
    // Validate inputs if needed
    console.log('submitted date: ' + date)
    console.log('submitted start time: ' + startTime)
    console.log('submitted end time: ' + startTime)

    // Create an event object
    const newEvent = {
      host: hostId,
      eventName,
      description,
      date: date,
      startTime: startTime,
      endTime: endTime,
      address,
      capacity: capacity, // Convert capacity to a number
    };
    console.log(newEvent)
    // Call the onSubmit function with the new event
    const sendForm = await eventApi.create(newEvent);
    const response = await sendForm;
    console.log(response)
  };

  return (
    <View style={styles.container}>
      <Text>Event Name</Text>
      <FlexInput
        placeholder="Event Name"
        value={eventName}
        onChangeText={(text) => setEventName(text)}
      />

      <Text>Description</Text>
      <FlexInput
        placeholder="Description"
        value={description}
        onChangeText={(text) => setDescription(text)}
      />

      <View style={styles.dateContainer}>
        <Text style={styles.dateLabel}>Date</Text>
        <DatePicker label="Date" date={date} onChange={handleDateChange} />
      </View>

      <View style={styles.dateContainer}>
        <Text style={styles.dateLabel}>Start Time</Text>
        <TimePicker label="Start Time" time={startTime} onChange={handleStartTimeChange} />
      </View>

      <View style={styles.dateContainer}>
        <Text style={styles.dateLabel}>End Time</Text>
        <TimePicker label="End Time" time={endTime} onChange={handleEndTimeChange} />
      </View>

      <Text>Address</Text>
      <FlexInput
        placeholder="Address"
        value={address}
        onChangeText={(text) => setAddress(text)}
      />

      <Text>Capacity</Text>
      <FlexInput
        placeholder="Capacity"
        value={capacity}
        onChangeText={(text) => setCapacity(text)}
        keyboardType="numeric"
      />

      <SubmitButton title="Submit" onPress={handleSubmit} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  dateContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginTop: 15,
    marginBottom: 15,
  },
  dateLabel: {
    marginRight: 10, // Add margin for spacing between label and date
  },
});

export default EventFormScreen;
