// ProfileBanner.js
import React from 'react';
import { ImageBackground, StyleSheet, Image, View, Text, Pressable } from 'react-native';
import { heightPercentageToDP as hp, widthPercentageToDP as wp } from 'react-native-responsive-screen';
import profilePlacholder from '../assets/profile-placeholder.jpg';
import NavButton from './NavButton';
import AsyncStorage from '@react-native-async-storage/async-storage';

const ProfileBanner = ({ user, setLoggedIn }) => {
  const handleLogOut = async () => {
    const clearingSession = await AsyncStorage.removeItem('@userId');
    if(clearingSession) {
        setLoggedIn(false);
    }
  }

  return (
    <ImageBackground source={require('../assets/profile-background.webp')} style={styles.banner}>
      <View style={styles.profileContainer}>
        <Pressable>
            <Text>Sign out</Text>
        </Pressable>
        <Image style={styles.profilePlacholder} source={profilePlacholder} />
      </View>
      <Text style={styles.fullName}>{user.fullName}</Text>
    </ImageBackground>
  );
};

const styles = StyleSheet.create({
  banner: {
    width: wp('100%'),
    minHeight: hp('33%'),
    alignItems: 'center',
    justifyContent: 'flex-end',
    paddingBottom: hp('4%'),
    marginBottom: hp('1%')
  },
  profileContainer: {
    width: 150,
    height: 150,
    borderRadius: 75,
    overflow: 'hidden',
  },
  profilePlacholder: {
    width: '100%',
    height: '100%',
  },
  fullName: {
    marginTop: 25,
    fontSize: 18,
    fontWeight: 'bold',
    color: 'white',
  },
  logout: {
    fontWeight: 'bold',
    color: 'white',
  }
});

export default ProfileBanner;
