// EventPictures.js
import React from 'react';
import { View, Image, ScrollView, StyleSheet, Dimensions } from 'react-native';
import { heightPercentageToDP as hp, widthPercentageToDP as wp } from 'react-native-responsive-screen';

const EventPictures = ({ pictures }) => {
  const { height } = Dimensions.get('window');

  return (
    <ScrollView horizontal style={[styles.imageContainer, { height }]}>
      {pictures.map((picture, index) => (
        <Image key={index} source={{ uri: picture }} style={styles.image} resizeMode="cover" />
      ))}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  imageContainer: {
    flexDirection: 'row',
    marginTop: 10,
  },
  image: {
    height: '100%', // Match the height of the ScrollView
    width: wp('60%'), // Set the desired width
    margin: 5,
  },
});

export default EventPictures;
