const getLocalTime = (utcTimestamp) => {
    const utcDate = new Date(utcTimestamp);
    const localDate = new Date(utcDate.getTime() + utcDate.getTimezoneOffset() * 60000);
  
    // You can customize the output format as needed
    const options = { year: 'numeric', month: 'numeric', day: 'numeric', hour: 'numeric', minute: 'numeric', second: 'numeric', timeZoneName: 'short' };
    const localTime = localDate.toLocaleString('en-US', options);
  
    return localTime;
  };
  
  // Example usage:
  const utcTimestamp = 1637272800000; // Replace this with your UTC timestamp
  const localTime = getLocalTime(utcTimestamp);
  console.log(localTime);
