import React, { useState } from 'react';
import { View, Text, Button, StyleSheet } from 'react-native';
import FlexInput from './FlexInput';
import SubmitButton from './SubmitButton';
import DateTimePicker from '@react-native-community/datetimepicker';

const EventFormScreen = ({ hostId, onSubmit }) => {
  const [eventName, setEventName] = useState('');
  const [description, setDescription] = useState('');
  const [date, setDate] = useState('');
  const [startTime, setStartTime] = useState('');
  const [endTime, setEndTime] = useState('');
  const [capacity, setCapacity] = useState('');
  const [tags, setTags] = useState('');

  const handleSubmit = () => {
    // Validate inputs if needed

    // Create an event object
    const newEvent = {
      hostId,
      eventName,
      description,
      date,
      startTime,
      endTime,
      capacity: parseInt(capacity), // Convert capacity to a number
      tags: tags.split(',').map((tag) => tag.trim()), // Convert tags to an array
    };

    // Call the onSubmit function with the new event
    onSubmit(newEvent);
  };

  const showDatepicker = () => {
    showMode('date');
  };

  return (
    <View style={styles.container}>
      <Text>Event Name</Text>
      <FlexInput
        placeholder="Event Name"
        value={eventName}
        onChangeText={(text) => setEventName(text)}
      />

      <Text>Description</Text>
      <FlexInput
        placeholder="Description"
        value={description}
        onChangeText={(text) => setDescription(text)}
      />

      <Text>Date</Text>
      <Button onPress={showDatepicker} title="Show date picker!" />


      <Text>Start Time</Text>
      <FlexInput
        placeholder="Start Time"
        value={startTime}
        onChangeText={(text) => setStartTime(text)}
      />

      <Text>End Time</Text>
      <FlexInput
        placeholder="End Time"
        value={endTime}
        onChangeText={(text) => setEndTime(text)}
      />

      <Text>Capacity</Text>
      <FlexInput
        placeholder="Capacity"
        value={capacity}
        onChangeText={(text) => setCapacity(text)}
        keyboardType="numeric"
      />

      <Text>Tags (comma-separated)</Text>
      <FlexInput
        placeholder="Tags"
        value={tags}
        onChangeText={(text) => setTags(text)}
      />

      <SubmitButton title="Submit" onPress={handleSubmit} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
});

export default EventFormScreen;
