import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet } from 'react-native';

import eventApi from '../api/eventApi';

const BrowseScreen = () => {
    const [events, setEvents] = useState(null)
    useEffect(() => {
        const fetchEvents = async () => {
            const eventsData = await eventApi.getEvents();
            const events = await eventsData;
            setEvents(events)
            console.log(events[0])
        }
        fetchEvents();
    }, [])

    return (
        <View style={styles.container}>
            <Text>Browse</Text>
            {events ? 
            
                <View>
                    <Text>{events[0].eventName}</Text>
                    <Text>{events[0].description}</Text>
                    <Text>{events[0].date}</Text>
                    <Text>{events[0].startTime}</Text>
                    <Text>{events[0].endTime}</Text>
                </View>

            : <Text>No event data</Text>}
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },
});

export default BrowseScreen;
