import React from 'react';
import { View, Text, FlatList, Image, StyleSheet } from 'react-native';
import UserWithOptions from './UserWithOptions';
import Toast from 'react-native-toast-message';

const RSVPList = ({ rsvps }) => {

    const showToast = (message) => {
        Toast.show({
            type: 'success',
            position: 'bottom',
            text1: message,
            visibilityTime: 2000,
        });
    };

    const onPressBlock = (blockedUser) => {
        // Implement your logic for blocking the user here
        // For now, let's just show a toast message
        alert('blocking')
        showToast(`Blocked user: ${blockedUser.username}`);
    };

    return (
        <View>
            <Text style={styles.headerText}>RSVPs:</Text>
            {rsvps ? (
                <FlatList
                    data={rsvps}
                    keyExtractor={(item, index) => item._id + index.toString()} // Combine _id and index
                    renderItem={({ item }) => (
                        <UserWithOptions
                            user={item}
                            onPressMessage={() => alert('Message RSVP')}
                            onPressBlock={() => onPressBlock(item)}
                            onPressAddFriend={() => alert('Add Friend RSVP')}
                        />
                    )}
                />
            ) : (
                <Text>Loading RSVPs...</Text>
            )}
        </View>
    );
};


const styles = StyleSheet.create({
    headerText: {
        fontSize: 18,
        fontWeight: 'bold',
        marginTop: 20,
        marginBottom: 10,
    },
    rsvpContainer: {
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'space-between',
        borderWidth: 1,
        borderRadius: 12,
        padding: 8,
        marginBottom: 5,
    },
    username: {
        marginRight: 10,
    },
    iconContainer: {
        flexDirection: 'row',
        alignItems: 'flex-end',
        justifyContent: 'flex-end',
    },
    icon: {
        width: 20,
        height: 20,
        marginLeft: 5,
    },
    headerText: {
        fontSize: 18,
        fontWeight: 'bold',
        marginTop: 20,
        marginBottom: 10,
    },
});

export default RSVPList;
