import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet } from 'react-native';

import eventApi from '../api/eventApi';
import { localTime, localDate } from '../utils/formatFunctions';

const BrowseScreen = () => {
    const [events, setEvents] = useState(null)
    useEffect(() => {
        const fetchEvents = async () => {
            try {
              const eventsData = await eventApi.getEvents();
              const events = await eventsData;
          
              const modifiedEvents = events.map(event => {
                const modifiedEvent = {
                  ...event,
                  date: localDate(event.date),
                  startTime: localTime(event.startTime), // Placeholder value to identify issues
                  endTime: localTime(event.endTime), // Placeholder value to identify issues
                };

                console.log(event.startTime)
          
                // Log the event and modifiedEvent for debugging
                console.log('Original Event:', event);
                console.log('Modified Event:', modifiedEvent);
          
                return modifiedEvent;
              });
          
              // Set the modified events
              setEvents(modifiedEvents);
          
              console.log(modifiedEvents[0]); // Log the first modified event
            } catch (error) {
              console.error('Error fetching events:', error);
            }
          };
        fetchEvents();
    }, [])

    return (
        <View style={styles.container}>
            <Text>Browse</Text>
            {events ? 
            
                <View>
                    <Text>{events[0].eventName}</Text>
                    <Text>{events[0].description}</Text>
                    <Text>{events[0].date}</Text>
                    <Text>{events[0].startTime}</Text>
                    <Text>{events[0].endTime}</Text>
                </View>

            : <Text>Issue Retreiving Events from database</Text>}
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },
});

export default BrowseScreen;
