// Function to convert UTC timestamp to formatted local date
export const localDate = (utcTimestamp) => {
  const utcDate = new Date(utcTimestamp);
  const options = { weekday: 'short', month: 'short', day: 'numeric' };
  const localDate = new Intl.DateTimeFormat('en-US', options).format(utcDate);
  
  console.log('Formatted Local Date:', localDate); // Log statement
  
  return localDate;
};


// Function to format local time as hh:mm
export const localTime = (localDateString) => {
  if (!localDateString) {
    return null;
  }

  const localDate = new Date(localDateString); // Convert the formatted string to a Date object

  const hours = localDate.getHours();
  const minutes = localDate.getMinutes();

  // Format hours and minutes with leading zeros
  const formattedHours = String(hours).padStart(2, '0');
  const formattedMinutes = String(minutes).padStart(2, '0');

  // Combine hours and minutes in 'hh:mm' format
  const formattedTime = `${formattedHours}:${formattedMinutes}`;

  return formattedTime;
};
