import React from 'react';
import { View, StyleSheet } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import NavButton from './NavButton';

const Navbar = () => {
  const navigation = useNavigation();

  // List of screens where the navbar should be hidden
  const screensWithoutNavbar = ['Login', 'Register'];

  // Check if the current screen is in the list of screens without the navbar
  const hideNavbar = screensWithoutNavbar.includes(navigation?.getCurrentRoute()?.name);

  if (hideNavbar) {
    return null; // Don't render the navbar on specified screens
  }

  return (
    <View style={styles.container}>
      <NavButton title="Browse" to='Browse' />
      <NavButton title="Profile" to='Profile' />
      {/* Add more NavButton components for additional screens */}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    alignItems: 'center',
    backgroundColor: 'lightgray', // Customize the background color
    height: 60, // Customize the height
  },
});

export default Navbar;
