import React from 'react';
import { View, Text, FlatList, Image, StyleSheet } from 'react-native';

const RSVPList = ({ rsvps }) => {
  return (
    <View>
      <Text style={styles.headerText}>RSVPs:</Text>
      {rsvps ? (
        <FlatList
          data={rsvps}
          keyExtractor={(item) => item._id}
          renderItem={({ item }) => (
            <View style={styles.rsvpContainer}>
              <Text style={styles.username}>{item.username}</Text>
              <View style={styles.iconContainer}>
                <Image source={require('../assets/message.png')} style={styles.icon} />
                <Image source={require('../assets/blockUser.png')} style={styles.icon} />
                <Image source={require('../assets/addFriend.png')} style={styles.icon} />
              </View>
            </View>
          )}
        />
      ) : (
        <Text>Loading RSVPs...</Text>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  headerText: {
    fontSize: 18,
    fontWeight: 'bold',
    marginTop: 20,
    marginBottom: 10,
  },
  rsvpContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    borderWidth: 1,
    borderRadius: 12,
    padding: 8,
    marginBottom: 5,
  },
  username: {
    marginRight: 10,
  },
  iconContainer: {
    flexDirection: 'row',
    alignItems: 'flex-end',
    justifyContent: 'flex-end',
  },
  icon: {
    width: 20,
    height: 20,
    marginLeft: 5,
  },
});

export default RSVPList;
