import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet, ScrollView } from 'react-native';

import EventCard from './EventCard';
import NavButton from './NavButton';

import eventApi from '../api/eventApi';

const BrowseScreen = ({userId, setMyRsvps}) => {
    const [events, setEvents] = useState(null);

    useEffect(() => {
        const fetchEvents = async () => {
            try {
                const eventData = await eventApi.getEvents();
                setEvents(eventData);
            } catch (error) {
                console.error('Error fetching events:', error);
            }
        };
        fetchEvents();
    }, [])

    return (
        <View style={styles.container}>
            <Text>Browse</Text>
            <NavButton title='Create Event' to='Create Event' />
            <ScrollView>
                {events ? (
                    events.map(event => <EventCard key={event._id} event={event} userId={userId} />)
                ) : (
                    <Text>Issue Retrieving Events from the database</Text>
                )}
            </ScrollView>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },
});

export default BrowseScreen;
