import React, { useState } from 'react';
import { View, Text, FlatList, StyleSheet } from 'react-native';
import UserWithOptions from './UserWithOptions';
import CustomAlert from './CustomAlert'; // Import your CustomAlert component
import blockUser from '../api/blockUser'; // Import the updated blockUser function
import { useAppContext } from './AppContext'; // Import the useAppContext hook

const RSVPList = ({ rsvps }) => {
  const { getCurrentUser } = useAppContext(); // Use the useAppContext hook to get getCurrentUser
  const [alertData, setAlertData] = useState(null);

  const showAlert = (type, message) => {
    setAlertData({ type, message });
  };

  const onPressBlock = async (blockedUser) => {
    try {
      const currentUser = getCurrentUser();
      const userId = currentUser._id;

      const blocking = await blockUser(userId, blockedUser._id);

      if (blocking.message === 'User blocked successfully') {
        showAlert('success', `Blocked user: ${blockedUser.username}`);
      } else if (blocking.message === 'User unblocked successfully') {
        showAlert('success', `Unblocked user: ${blockedUser.username}`);
      }
    } catch (error) {
      showAlert('error', 'Failed to block/unblock user');
      console.error('Error blocking/unblocking user:', error);
    }
  };

  return (
    <View>
      <Text style={styles.headerText}>RSVPs:</Text>
      {rsvps ? (
        <FlatList
          data={rsvps}
          keyExtractor={(item, index) => item._id + index.toString()}
          renderItem={({ item }) => (
            <UserWithOptions
              user={item}
              onPressMessage={() => alert('Message RSVP')}
              onPressBlock={() => onPressBlock(item)}
              onPressAddFriend={() => alert('Add Friend RSVP')}
            />
          )}
        />
      ) : (
        <Text>Loading RSVPs...</Text>
      )}

      {/* Display CustomAlert if there is an alertData */}
      {alertData && (
        <CustomAlert
          message={alertData.message}
          success={alertData.type === 'success'}
          onClose={() => setAlertData(null)} // Handle the onClose event
        />
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  headerText: {
    fontSize: 18,
    fontWeight: 'bold',
    marginTop: 20,
    marginBottom: 10,
  },
});

export default RSVPList;
