import React from 'react';
import { View, ImageBackground, StyleSheet, Image, Text } from 'react-native';
import profileBackground from '../assets/profile-background.webp';
import profilePlacholder from '../assets/profile-placeholder.jpg';
import { heightPercentageToDP as hp, widthPercentageToDP as wp } from 'react-native-responsive-screen';


const ProfileScreen = ({ route }) => {
  console.log(route)
  // Extract user object from the route parameters
  const { user } = route.params;
  console.log(user)

  return (
    <View style={styles.container}>

      <View style={styles.banner} source={profileBackground}>

        <ImageBackground source={profileBackground} style={styles.banner}>
          <View style={styles.profileContainer}>
            <Image style={styles.profilePlacholder} source={profilePlacholder} />
          </View>
          <Text style={styles.fullName}>{user.fullName}</Text>
        </ImageBackground>
      </View>
      <Text style={styles.label}>Hosting</Text>

      <Text style={styles.label}>Attending</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    paddingLeft: 15,
    paddingRight: 15,
    paddingBottom: 15,
  },
  banner: {
    width: wp('100%'), // This makes the container take up 100% of the screen width
    minHeight: hp('33%'),
    alignItems: 'center',
    justifyContent: 'flex-end',
    paddingBottom: hp('4%'),
  },
  profileContainer: {
    width: 150, // Adjust the width and height based on your preference
    height: 150,
    borderRadius: 75, // Make it half of the width and height for a perfect circle
    overflow: 'hidden', // This is important for the borderRadius to take effect
  },
  profilePlacholder: {
    width: '100%', // Take up the full width of the container
    height: '100%', // Take up the full height of the container
  },
  fullName: {
    marginTop: 25,
    fontSize: 18,
    fontWeight: 'bold',
    color: 'white',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  label: {
    alignSelf: 'flex-start', // Align the labels to the start (left) of the container
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 10,
    color: 'black', // Set label color based on your design
  },
});

export default ProfileScreen;
