import React, { useState } from 'react';
import { View, StyleSheet } from 'react-native';
import FlexInput from './FlexInput';
import SubmitButton from './SubmitButton';
import CustomAlert from './CustomAlert';

import userApi from '../api/userApi';

const LoginForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [response, setResponse] = useState(null);

  const handleLogin = async () => {
    console.log('Username:', username);
    console.log('Password:', password);

    const sendLogin = await userApi.login(username, password);
    const response = await sendLogin;
    console.log(response);
    if(response.message) {
        console.log("Login Message: " + response.message)
    }

    // You can add logic to show a toast message here based on the response
  };

  return (
    <View style={styles.container}>
      <FlexInput
        type="text"
        placeholder="Username or Email"
        value={username}
        onChangeText={(text) => setUsername(text)}
      />
      <FlexInput
        type="password"
        placeholder="Password"
        value={password}
        onChangeText={(text) => setPassword(text)}
      />
      <SubmitButton title="Login" onPress={handleLogin} 
      />
      {response && <CustomAlert response={response} />}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    padding: 20,
    minWidth: '100%'
  },
});

export default LoginForm;
