// ProfileScreen.js
import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet, ScrollView } from 'react-native';
import ProfileBanner from './ProfileBanner'; // Adjust the path based on your project structure
import userApi from '../api/userApi';
import EventCard from './EventCard';

const ProfileScreen = ({ route }) => {
  const { user } = route.params;
  console.log('\n\n\n\n', user.events, '\n\n\n\n')
  console.log('\n\n\n\n', user.rsvps, '\n\n\n\n')
  const [events, setEvents] = useState(null);
  const [rsvps, setRsvps] = useState(null);

  useEffect(() => {
    /// set user events
    const fetchEventsData = async () => {
      const eventsData = await userApi.getMyEvents(user._id);
      setEvents(eventsData);
      // const rsvpData = await userApi.getMyRsvps(user._id);
      // setRsvps(rsvpData);
    }
    fetchEventsData();
    // set user rsvps
  }, [])

  return (
    <View style={styles.container}>
      <ProfileBanner user={user} />

      <ScrollView>
        <Text style={styles.label}>Hosting</Text>
        <ScrollView horizontal showsHorizontalScrollIndicator={false} style={styles.scrollView}>
          {events &&
            events.map((event) => (
              <EventCard key={event._id} event={event} />
            ))}
        </ScrollView>
        <Text style={styles.label}>Attending</Text>
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    paddingLeft: 10,
    paddingRight: 10,
    paddingBottom: 10,
  },
  label: {
    alignSelf: 'flex-start',
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 10,
    color: 'black',
  },
  scrollView: {
    width: '100%',
  },
});

export default ProfileScreen;
