import React, { useState } from 'react';
import { View, Image, StyleSheet, TouchableOpacity, Modal, Text } from 'react-native';
import Carousel, { Pagination } from 'react-native-snap-carousel';
import { heightPercentageToDP as hp, widthPercentageToDP as wp } from 'react-native-responsive-screen';

const EventPictures = ({ pictures }) => {
  const [modalVisible, setModalVisible] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);
  const [activeSlide, setActiveSlide] = useState(0);

  const handleImagePress = (index) => {
    setSelectedImage(pictures[index]);
    setModalVisible(true);
  };

  const closeModal = () => {
    setModalVisible(false);
    setSelectedImage(null);
  };

  const renderCarouselItem = ({ item, index }) => (
    <TouchableOpacity onPress={() => handleImagePress(index)}>
      <Image source={{ uri: item }} style={styles.carouselImage} resizeMode="cover" />
    </TouchableOpacity>
  );

  return (
    <View>
      <Carousel
        data={pictures}
        renderItem={renderCarouselItem}
        sliderWidth={wp('100%')}
        itemWidth={wp('80%')}
        onSnapToItem={(index) => setActiveSlide(index)}
      />
      <Pagination
        dotsLength={pictures.length}
        activeDotIndex={activeSlide}
        containerStyle={styles.paginationContainer}
        dotStyle={styles.paginationDot}
        inactiveDotStyle={styles.paginationInactiveDot}
        inactiveDotOpacity={0.4}
        inactiveDotScale={0.6}
      />

      {/* Modal for displaying selected image */}
      <Modal visible={modalVisible} transparent={true} onRequestClose={closeModal}>
        <View style={styles.modalContainer}>
          <TouchableOpacity style={styles.closeButton} onPress={closeModal}>
            <Text style={styles.closeButtonText}>Close</Text>
          </TouchableOpacity>
          <Image style={styles.modalImage} source={{ uri: selectedImage }} resizeMode="contain" />
        </View>
      </Modal>
    </View>
  );
};

const styles = StyleSheet.create({
  carouselImage: {
    height: hp('30%'),
    width: wp('80%'),
    borderRadius: 10,
    marginHorizontal: wp('2%'),
  },
  paginationContainer: {
    position: 'absolute',
    bottom: hp('2%'),
    alignSelf: 'center',
  },
  paginationDot: {
    width: wp('3%'),
    height: wp('3%'),
    borderRadius: wp('1.5%'),
    marginHorizontal: wp('1%'),
    backgroundColor: 'rgba(255, 255, 255, 0.92)',
  },
  paginationInactiveDot: {
    backgroundColor: 'rgba(255, 255, 255, 0.5)',
  },
  modalContainer: {
    flex: 1,
    backgroundColor: 'black',
    justifyContent: 'center',
    alignItems: 'center',
  },
  closeButton: {
    position: 'absolute',
    top: hp('5%'), // Adjusted the top position
    right: wp('2%'),
    zIndex: 1,
  },
  closeButtonText: {
    color: 'white',
    fontSize: wp('4%'),
  },
  modalImage: {
    width: wp('100%'),
    height: hp('80%'),
  },
});

export default EventPictures;
