import React, { useState } from 'react';
import { View, Text, TextInput, Button, StyleSheet } from 'react-native';

const EventFormScreen = ({ hostId, onSubmit }) => {
  const [formData, setFormData] = useState({
    eventName: '',
    description: '',
    date: '',
    startTime: '',
    endTime: '',
    capacity: '',
    tags: '',
  });

  const handleChange = (field, value) => {
    setFormData({ ...formData, [field]: value });
  };

  const handleSubmit = () => {
    const newEvent = {
      hostId,
      ...formData,
      capacity: parseInt(formData.capacity) || 0,
      tags: formData.tags.split(',').map((tag) => tag.trim()),
    };
    onSubmit(newEvent);
  };

  return (
    <View style={styles.container}>
      {['Event Name', 'Description', 'Date', 'Start Time', 'End Time', 'Capacity', 'Tags (comma-separated)'].map((label, index) => (
        <View key={index}>
          <Text>{label}</Text>
          <TextInput
            style={styles.input}
            value={formData[label.toLowerCase().replace(/ /g, '')]}
            onChangeText={(text) => handleChange(label.toLowerCase().replace(/ /g, ''), text)}
            keyboardType={label === 'Capacity' ? 'numeric' : 'default'}
          />
        </View>
      ))}

      <Button title="Submit" onPress={handleSubmit} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  input: {
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    padding: 5,
  },
});

export default EventFormScreen;
