import React, { useState } from 'react';
import { View, StyleSheet } from 'react-native';
import FlexInput from './FlexInput';
import SubmitButton from './SubmitButton';

import userApi from '../api/userApi';

const RegisterForm = () => {
    const [email, setEmail] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');

    const handleRegister = async () => {
        console.log('Username:', username);
        console.log('Password:', password);

        // Add your registration logic or API calls as needed
        const sendRegister = await userApi.register(username, password);
        const response = await sendRegister;
        console.log(response);

        // You can add logic to show a toast message here based on the response
    };

    return (
        <View style={styles.container}>
            
            <Text>Username</Text>
            <FlexInput
                type="text"
                placeholder="username"
                value={username}
                onChangeText={(text) => setUsername(text)}
            />
            <Text>Email</Text>
            <FlexInput
                type="text"
                placeholder="email address"
                value={email}
                onChangeText={(text) => setEmail(text)}
            />
            <Text>Password</Text>
            <FlexInput
                type="password"
                placeholder="password"
                value={password}
                onChangeText={(text) => setPassword(text)}
            />
            <Text>Confirm Password</Text>
            <FlexInput
                type="password"
                placeholder= "password again"
                value={confirmPassword}
                onChangeText={(text) => setConfirmPassword(text)}
            />
            <SubmitButton
                onPress={handleRegister}
                title="Sign Up"
            />
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        padding: 20,
    },
});

export default RegisterForm;
