// Function to convert UTC timestamp to local date
export const localDate = (utcTimestamp) => {
  const utcDate = new Date(utcTimestamp);
  const localDate = new Date(utcDate.getTime() + utcDate.getTimezoneOffset() * 60000);
  console.log(localDate)
  return localDate;

};

// Function to format local time as hh:mm am/pm
export const localTime = (localDate) => {
  if (!localDate) {
    return null;
  }

  const options = { hour: 'numeric', minute: 'numeric', hour12: true };
  const localTime = localDate.toLocaleString('en-US', options);
  return localTime;
};
