import React, { useEffect, useState } from 'react';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet } from 'react-native';
import { RootSiblingParent } from 'react-native-root-siblings';

import RegisterScreen from './components/RegisterScreen';
import LoginScreen from './components/LoginScreen';
import ProfileScreen from './components/ProfileScreen';
import Navbar from './components/Navbar';
import BrowseScreen from './components/BrowseScreen';
import EventFormScreen from './components/EventFormScreen';
import EventDetails from './components/EventDetails';

import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';

const Stack = createStackNavigator();

export default function App() {
  const [loggedIn, setLoggedIn] = useState(null)
  const [user, setUser] = useState(null);

  useEffect(() => {
    console.log('setting user')

  }, [user])

  return (
    <RootSiblingParent style={styles.container}>
      <NavigationContainer>
        <Stack.Navigator initialRouteName="Login">
          <Stack.Screen name="Profile" component={ProfileScreen} />
          <Stack.Screen name="Browse">
            {(props) => <BrowseScreen {...props} getUserId={() => user?.id} />}
          </Stack.Screen>

          <Stack.Screen name="Create Event">
            {(props) => <EventFormScreen {...props} getHostId={() => user?.id} />}
          </Stack.Screen>
          <Stack.Screen name="Login">
            {(props) => <LoginScreen {...props} setLoggedIn={setLoggedIn} setUser={setUser} />}
          </Stack.Screen>
          <Stack.Screen name="Register">
            {(props) => <RegisterScreen {...props} setLoggedIn={setLoggedIn} />}
          </Stack.Screen>
          <Stack.Screen name="Event Details" component={EventDetails} />
        </Stack.Navigator>
        <Navbar isLoggedIn={loggedIn} user={user} />
        <StatusBar style="auto" />
      </NavigationContainer>
    </RootSiblingParent>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    height: '100%'
  },
});
