import React, { useState } from 'react';
import { View, Text, StyleSheet } from 'react-native';
import FlexInput from './FlexInput';
import SubmitButton from './SubmitButton';

import validateRegistrationForm from '../utils/validateRegistrationForm';
import userApi from '../api/userApi';

const RegisterForm = () => {
    const [email, setEmail] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [validationErrors, setValidationErrors] = useState({});

    const handleRegister = async () => {
        // Validate the form
        const errors = validateForm(username, email, password, confirmPassword);
        setValidationErrors(errors);

        // If there are validation errors, stop the registration process
        if (Object.keys(errors).length > 0) {
            return;
        }

        // Proceed with the registration logic
        const sendRegister = await userApi.register(username, email, password);
        const response = await sendRegister;
        console.log(response);

        // You can add logic to show a toast message here based on the response
    };

    return (
        <View style={styles.container}>
            <Text style={styles.text}>Username</Text>
            <FlexInput
                type="text"
                value={username}
                onChangeText={(text) => setUsername(text)}
            />
            {validationErrors.username && <Text style={styles.error}>{validationErrors.username}</Text>}

            <Text style={styles.text}>Email</Text>
            <FlexInput
                type="text"
                value={email}
                onChangeText={(text) => setEmail(text)}
            />
            {validationErrors.email && <Text style={styles.error}>{validationErrors.email}</Text>}

            {/* Repeat the pattern for other input fields */}

            <SubmitButton onPress={handleRegister} title="Sign Up" />
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        padding: 20,
    },
    text: {
        fontSize: 16,
        marginBottom: 5,
        color: '#333', // You can change the color to your preference
    },
});

export default RegisterForm;
