// AppContext.js
import React, { createContext, useContext, useState } from 'react';

const AppContext = createContext();

export const AppProvider = ({ children }) => {
  const [loggedIn, setLoggedIn] = useState(null);
  const [myRsvps, setMyRsvps] = useState(null);
  const [myEvents, setMyEvents] = useState(null);
  const [browseEvents, setBrowseEvents] = useState(null);
  const [user, setUser] = useState(null);

  const getCurrentUser = () => {
    return user;
  };

  const contextValue = {
    loggedIn,
    setLoggedIn: (value) => {
      console.log('Setting loggedIn:', value);
      setLoggedIn(value);
    },
    myRsvps,
    setMyRsvps: (value) => {
      console.log('Setting myRsvps:', value);
      setMyRsvps(value);
    },
    myEvents,
    setMyEvents: (value) => {
      console.log('Setting myEvents:', value);
      setMyEvents(value);
    },
    browseEvents,
    setBrowseEvents: (value) => {
      console.log('Setting browseEvents:', value);
      setBrowseEvents(value);
    },
    user,
    setUser: (value) => {
      console.log('Setting user:', value);
      setUser(value);
    },
    getCurrentUser,
  };

  return <AppContext.Provider value={contextValue}>{children}</AppContext.Provider>;
};

export const useAppContext = () => {
  const context = useContext(AppContext);
  if (!context) {
    throw new Error('useAppContext must be used within an AppProvider');
  }
  return context;
};
