import React, { useEffect } from 'react';
import Toast from 'react-native-root-toast';

const CustomAlert = ({ response }) => {
  useEffect(() => {
    Toast.show(response.message, {
      duration: Toast.durations.LONG,
      position: Toast.positions.BOTTOM,
      backgroundColor: 'red',
      textColor: 'white',
    });
  }, [response]);

