  };

  const showErrorToast = (message) => {
    Toast.show(message, {
      duration: Toast.durations.LONG,
      position: Toast.positions.BOTTOM,
      backgroundColor: 'red',
      textColor: 'white',
    });
  };

  return null;
};

export default CustomAlert;
